import React, { memo, useState, useRef } from 'react'
import { clipImgSize } from '@/utils/tools'
import LazyLoad from 'react-lazyload'
import lazyLoadImg from '@/assets/img/lazyload.jpg'
import './index.less'
//404 图片
const notFound =
  'data:image/png;base64,'
//图片懒加载组件
export default memo(function LazyLoadImg(props) {
  //props
  //图片地址 宽高
  const { url, width, height } = props
  //ref
  //获取图片ref音乐
  const imgRef = useRef()
  //state
  //控制懒加载图片显示和隐藏
  const [show, setShow] = useState(true)
  //functions
  // 图片加载完成 替换懒加载图片
  const showImage = () => {
    setShow(false)
  }
  //图片加载失败 显示404
  const handleError = () => {
    imgRef.current.src = notFound
  }
  return (
    <div className='lazyload-container'>
      {show ? (
        <img
          src={lazyLoadImg}
          alt=''
          className='lazyload-img'
          style={{ width: width + 'px', height: height + 'px' }}
        />
      ) : null}
      <LazyLoad
        style={{ width: width + 'px', height: height + 'px' }}
        scroll={true}
        offset={100}
      >
        <img
          ref={imgRef}
          src={`${url}${clipImgSize(width, height)}`}
          alt=''
          className='real-img'
          style={{ width: width + 'px', height: height + 'px' }}
          onLoad={() => {
            showImage()
          }}
          onError={() => {
            handleError()
          }}
        />
      </LazyLoad>
    </div>
  )
})
